"use strict";function textTruncate(t){for(var e=document.querySelectorAll(t),n=0;n<e.length;n++){var r=e[n].textContent.trim();if(!(r.length>180))return r;var o=r.substring(0,180)+" ...";e[n].innerHTML=o}}function toggleHamberger(){$(".toggleMenu").on("click",function(){$("#navbarNavDropdown").toggleClass("show"),$(this).toggleClass("active")})}textTruncate(".perspectives-body"),toggleHamberger();